package lab2;import java.util.Scanner;public class Lab2_Bai5_buoi2_lop {    public static void main(String[] args) {        Bai5_lab2();    }    public static void Bai5_lab2() {        Scanner sc = new Scanner(System.in);                System.out.print("Nhap ho va ten: ");        String hoTen = sc.nextLine();        System.out.print("Nhap diem toan: ");        double toan = sc.nextDouble();        System.out.print("Nhap diem ly: ");        double ly = sc.nextDouble();        System.out.print("Nhap diem hoa: ");        double hoa = sc.nextDouble();        String xepLoai = null;        double diemTB = tinhDiemTb(toan, ly, hoa);        xepLoai = xepLoai(diemTB);        System.out.printf("Sinh vien %s dat diem TB %.2f xep loai %s", hoTen, diemTB, xepLoai);    }    public static String xepLoai(double diemTB) {        String xepLoai;        if (10 >= diemTB && diemTB >= 9) {            xepLoai = "Xuat Sac";        } else if (9 > diemTB && diemTB >= 8) {            xepLoai = "Gioi";        } else if (8 > diemTB && diemTB >= 7) {            xepLoai = "Kha";        } else if (7 > diemTB && diemTB >= 5) {            xepLoai = "Trung Binh";        } else {            xepLoai = "Yeu";        }        return xepLoai;    }    public static double tinhDiemTb(double mon1,            double mon2,            double mon3) {        return ((mon1 + mon2 + mon3) / 3);    }}